<?php
 namespace Fuzhi\Controller; use User\Api\UserApi; class UserController extends HomeController { public function index(){ } public function register($username = '', $password = '', $repassword = '', $email = '', $verify = ''){ if(!C('USER_ALLOW_REGISTER')){ $this->error('注册已关闭'); } if(IS_POST){ if(!check_verify($verify)){ $this->error('验证码输入错误！'); } if($password != $repassword){ $this->error('密码和重复密码不一致！'); } $User = new UserApi; $uid = $User->register($username, $password, $email); if(0 < $uid){ $this->success('注册成功！',U('login')); } else { $this->error($this->showRegError($uid)); } } else { $this->display(); } } public function login($username = '', $password = '', $verify = ''){ if(IS_POST){ if(!check_verify($verify)){ $this->error('验证码输入错误！'); } $user = new UserApi; $uid = $user->login($username, $password); if(0 < $uid){ $Member = D('Member'); if($Member->login($uid)){ $this->success('登录成功！',U('Home/Index/index')); } else { $this->error($Member->getError()); } } else { switch($uid) { case -1: $error = '用户不存在或被禁用！'; break; case -2: $error = '密码错误！'; break; default: $error = '未知错误！'; break; } $this->error($error); } } else { $this->display(); } } public function logout(){ if(is_login()){ D('Member')->logout(); $this->success('退出成功！', U('User/login')); } else { $this->redirect('User/login'); } } public function verify(){ $verify = new \Think\Verify(); $verify->entry(1); } private function showRegError($code = 0){ switch ($code) { case -1: $error = '用户名长度必须在16个字符以内！'; break; case -2: $error = '用户名被禁止注册！'; break; case -3: $error = '用户名被占用！'; break; case -4: $error = '密码长度必须在6-30个字符之间！'; break; case -5: $error = '邮箱格式不正确！'; break; case -6: $error = '邮箱长度必须在1-32个字符之间！'; break; case -7: $error = '邮箱被禁止注册！'; break; case -8: $error = '邮箱被占用！'; break; case -9: $error = '手机格式不正确！'; break; case -10: $error = '手机被禁止注册！'; break; case -11: $error = '手机号被占用！'; break; default: $error = '未知错误'; } return $error; } public function profile(){ if ( !is_login() ) { $this->error( '您还没有登陆',U('User/login') ); } if ( IS_POST ) { $uid = is_login(); $password = I('post.old'); $repassword = I('post.repassword'); $data['password'] = I('post.password'); empty($password) && $this->error('请输入原密码'); empty($data['password']) && $this->error('请输入新密码'); empty($repassword) && $this->error('请输入确认密码'); if($data['password'] !== $repassword){ $this->error('您输入的新密码与确认密码不一致'); } $Api = new UserApi(); $res = $Api->updateInfo($uid, $password, $data); if($res['status']){ $this->success('修改密码成功！'); }else{ $this->error($res['info']); } }else{ $this->display(); } } function checkLogin() { $ret = ''; if(is_login()) { $ret = '{"success":true,"code":null,"msg":null,"t":null}'; } else { $ret = '{"success":false    ,"code":null,"msg":null,"t":null}'; } echo $ret; } function getLoginUserInfo() { $ret = '{"success":false,"code":null,"msg":"參數異常","t":null}'; echo $ret; } function subFeedback() { $ret = '{"success":true}'; $Action = D('Feedback'); $Action->update(); echo $ret; } } 